<?php
require_once 'PHPUnit/Framework.php';

require_once dirname(__FILE__).'/../../../../library/Soulex/Invoke/Parser.php';

/**
 * Test class for Soulex_Invoke_Parser.
 * Generated by PHPUnit on 2010-05-06 at 23:24:35.
 */
class Soulex_Invoke_ParserTest extends ControllerTestCase {
    /**
     * @var    Soulex_Invoke_Parser
     * @access protected
     */
    protected $object;

    /**
     * Sets parser object, calls parent::setUp() method for bootstraping application
     *
     * @access public
     */
    public function setUp() {
        $this->object = Soulex_Invoke_Parser::getInstance();
        parent::setUp();
    }

    /**
     *
     * @access public
     */
    public function tearDown() {
        parent::tearDown();
    }

    /**
     * testGetInstance().
     */
    public function testGetInstance() {
        $this->assertClassHasStaticAttribute('_parser', 'Soulex_Invoke_Parser');
    }

    /**
     * @todo Implement test DB actions and controllers.
     */
    public function testParse() {
        
        $content = 'some text here
            not having marker
            another text';
        $parsedContent = $this->object->parse($content);
        $testContent = 'some text here
            not having marker
            another text';
        $this->assertEquals($testContent, $parsedContent);
        
        $content = 'some text here
            <!!invoke action(1)!!>
            another text';
        $parsedContent = $this->object->parse($content);
        $testContent = 'some text here
            this is foo action of foo controller invoked by parser!
            another text';
        $this->assertEquals($testContent, $parsedContent);

    }
}
